package cos.mos.toolkit.json;import com.google.gson.JsonDeserializationContext;import com.google.gson.JsonDeserializer;import com.google.gson.JsonElement;import com.google.gson.JsonParseException;import com.google.gson.JsonPrimitive;import com.google.gson.JsonSerializationContext;import com.google.gson.JsonSerializer;import java.lang.reflect.Type;import cos.mos.toolkit.java.UText;/** * @Description gson解析时，如果 Long==null，则返回0L * @Author Kosmos * @Date 2019-11-07 11:24 * @Email KosmoSakura@gmail.com */public class LongConverter implements JsonSerializer<Long>, JsonDeserializer<Long> {    @Override    public JsonElement serialize(Long src, Type typeOfSrc, JsonSerializationContext context) {        return new JsonPrimitive(UText.isNull(src, 0L));    }    @Override    public Long deserialize(JsonElement json, Type typeOfT, JsonDeserializationContext context) throws JsonParseException {        return json.getAsJsonPrimitive().getAsLong();    }}