package cos.mos.toolkit.json;import com.google.gson.JsonDeserializationContext;import com.google.gson.JsonDeserializer;import com.google.gson.JsonElement;import com.google.gson.JsonParseException;import com.google.gson.JsonPrimitive;import com.google.gson.JsonSerializationContext;import com.google.gson.JsonSerializer;import java.lang.reflect.Type;import cos.mos.toolkit.java.UText;/** * @Description gson解析时，如果 String==null，则返回"" * @Author Kosmos * @Date 2019-11-29 10:21 * @Email KosmoSakura@gmail.com */public class StringConverter implements JsonSerializer<String>, JsonDeserializer<String> {    @Override    public JsonElement serialize(String src, Type typeOfSrc, JsonSerializationContext context) {        return new JsonPrimitive(UText.isNull(src));    }    @Override    public String deserialize(JsonElement json, Type typeOfT, JsonDeserializationContext context) throws JsonParseException {        return json.getAsJsonPrimitive().getAsString();    }}